html{font-family:sans-serif;-ms-text-size-adjust:100%;-webkit-text-size-adjust:100%}body{margin:0}b{font-weight:700}img{border:0}html{box-sizing:border-box;height:100%}*,:after,:before{box-sizing:inherit}body{font-family:Roboto,sans-serif;height:100%}.wa-container{width:25pc;height:711px;position:relative;top:50%;left:50%;-webkit-transform:translate(-50%, -50%);-ms-transform:translate(-50%, -50%);transform:translate(-50%, -50%)}.wa-container .status-bar{height:25px;background:#054d44;color:#fff;font-size:14px;padding:0 8px}.wa-container .status-bar:after{content:"";display:table;clear:both}.wa-container .status-bar div{float:right;position:relative;top:50%;-webkit-transform:translateY(-50%);-ms-transform:translateY(-50%);transform:translateY(-50%);margin:0 0 0 8px}.wa-container .user-bar{height:55px;background:#075e54;color:#fff;padding:0 8px;font-size:24px}.wa-container .user-bar:after{content:"";display:table;clear:both}.wa-container .user-bar div{float:left;position:relative;top:50%;-webkit-transform:translateY(-50%);-ms-transform:translateY(-50%);transform:translateY(-50%)}.wa-container .user-bar div.actions{float:right;margin:0 0 0 20px}.wa-container .user-bar div.actions.more{margin:0 9pt 0 20px}.wa-container .user-bar div.actions.attachment i{display:block;-webkit-transform:rotate(-45deg);-ms-transform:rotate(-45deg);transform:rotate(-45deg)}.wa-container .user-bar .user{margin:0 0 0 8px;width:36px;height:36px}.wa-container .user-bar .user img{border-radius:50%;display:block;box-shadow:0 1px 0 hsla(0,0%,100%,.1);width:100%}.wa-container .user-bar .user-name{font-size:1pc;margin:0 0 0 8px}.wa-container .user-bar .user-name span{display:block;font-size:9pt}.wa-container .chat-window{height:584px;position:relative;background:url(http://img3.todoiphone.net/wp-content/uploads/2014/03/WhatsApp-Wallpaper-39.jpg);background-size:100%;padding:0 1pc}.wa-container .chat-window:after{content:"";display:table;clear:both}.wa-container .chat-window .balloon{padding:8px;position:relative;color:#000;font-size:14px;line-height:18px;box-shadow:0 0 1px rgba(0,0,0,.2);clear:both;margin:8px 0;max-width:85%}.wa-container .chat-window .balloon:after{position:absolute;content:"";width:0;height:0;border-style:solid}.wa-container .chat-window .balloon .data{display:inline-block;padding:0 20px 0 1pc;font-size:11px;color:#b9b9b9;position:relative;bottom:-5px;float:right;background-repeat:no-repeat;background-position:right 3px;background-color:transparent;background-size:1pc auto}.wa-container .chat-window .balloon .data.grey{background-image:url(../images/dist/singlegrey.png);background-size:9pt;padding:0 14px 0 1pc;background-position:right 2px}.wa-container .chat-window .balloon:first-child{margin:1pc 0 8px}.wa-container .chat-window .balloon.him{background:#fff;float:left;border-radius:0 5px 5px 5px}.wa-container .chat-window .balloon.him .data{padding:0 0 0 1pc}.wa-container .chat-window .balloon.him:after{top:0;left:-10px;border-width:0 10px 10px 0;border-color:transparent #fff transparent transparent}.wa-container .chat-window .balloon.you{background:#e1ffc7;float:right;border-radius:5px 0 5px 5px}.wa-container .chat-window .balloon.you:after{top:0;right:-10px;border-width:0 0 10px 10px;border-color:transparent transparent transparent #e1ffc7}